import { Component, OnInit } from '@angular/core';
import { PaymentMethod } from '../models/paymentMethod';
import { FormControl } from '@angular/forms';
import { Cart } from '../models/cart';
import { Payment } from '../models/payment';
import { NavigationService } from '../services/navigation.service';
import { Router } from '@angular/router';
import { UserAuthenticationService } from '../services/user-authentication.service';
import { CartService } from '../services/cart.service';
import { Order } from '../models/order';
import { timer } from 'rxjs';

@Component({
  selector: 'app-order',
  templateUrl: './order.component.html',
  styleUrls: ['./order.component.css']
})
export class OrderComponent implements OnInit {
  selectedPaymentMethodName = '';
  selectedPaymentMethod = new FormControl('0');

  address = '';
  mobileNumber = '';
  displaySpinner = false;
  message = ''

  paymentMethods: PaymentMethod[] = [];

  usersCart: Cart = {
    id: 0,
    user: this.userAuthentication.getUser(),
    cartItems: [],
    ordered: false,
    orderedOn: ''
  }

  userPaymentInfo: Payment = {
    id: 0,
    user: this.userAuthentication.getUser(),
    paymentMethod: {
      id: 0,
      type: '',
      provider: '',
      available: false,
      reason: ''
    },
    totalAmount: 0,
    shipingCharges: 0,
    amountReduced: 0,
    amountPaid: 0,
    createdAt: ''
  }
  classname: string;

  constructor(
    private navigationService: NavigationService,
    public userAuthentication: UserAuthenticationService,
    public cartService: CartService,
    private router: Router
  ) { }


  ngOnInit(): void {
    // get payment
    this.navigationService.getPaymentMethods().subscribe((res) => {
      this.paymentMethods = res
    })

    this.selectedPaymentMethod.valueChanges.subscribe((res: any) => {
      if (res === '0') {
        this.selectedPaymentMethodName = '';
      }
      else {
        this.selectedPaymentMethodName = res.toString();
      }
    });
    // get cart

    this.navigationService.getActiveCartOfUser
      (this.userAuthentication.getUser().id)
      .subscribe((res: any) => {
        this.usersCart = res;
        this.cartService.calculatePayment(res, this.userPaymentInfo)
      });

    // set address and phone number
    this.address = this.userAuthentication.getUser().address;
    this.mobileNumber = this.userAuthentication.getUser().mobile;

  }

  getPaymentMethod(id: string) {
    let x = this.paymentMethods.find((v) =>
      v.id === parseInt(id))
    return x?.type + ' - ' + x?.provider
  }

  placeOrder() {
    this.displaySpinner = true;
    let isPaymentSuccessfull = this.payMoney()

    if (!isPaymentSuccessfull) {
      this.displaySpinner = false;
      this.message = 'Something went wrong'
      this.classname = 'text-danger'
      return
    }

    let step = 0;
    let count = timer(0, 300).subscribe((res) => {
      step++;
      if (step === 1) {
        this.message = 'processing payment'
        this.classname = 'text-success'
      }
      if (step === 2) {
        this.message = 'payment successfull, order is being placed'
        this.storeOrder()

      }
      if (step === 3) {
        this.message = 'your order has been placed'
        this.displaySpinner = false
      }
      if (step === 4) {
        this.router.navigateByUrl('/home')
        count.unsubscribe()

      }
    })
  }

  payMoney() {
    return true;
  }
  storeOrder() {
    let payment: Payment;
    let pmid = 0;
    if (this.selectedPaymentMethod.value) {
      pmid = parseInt(this.selectedPaymentMethod.value)
    }

    payment = {
      id: 0,
      paymentMethod: {
        id: pmid,
        type: '',
        provider: '',
        available: false,
        reason: ''
      },
      user: this.userAuthentication.getUser(),
      totalAmount: this.userPaymentInfo.totalAmount,
      shipingCharges: this.userPaymentInfo.shipingCharges,
      amountReduced: this.userPaymentInfo.amountReduced,
      amountPaid: this.userPaymentInfo.amountPaid,
      createdAt: ''
    }
    this.navigationService
      .insertPayment(payment)
      .subscribe((paymentResponse: any) => {
        payment.id = parseInt(paymentResponse)
        let order: Order = {
          id: 0,
          user: this.userAuthentication.getUser(),
          cart: this.usersCart,
          payment: payment,
          createdAt: ''
        }
        this.navigationService.insertOrder(order).subscribe((orderResponse)=>{
          this.cartService.changeCart.next(0)
        })
      })
  }



}
<div class="my-container mx-auto py-5">
  <section class="m-3 p-4 border border-dark my-rounded-corner">
    <h4 class="border-secondary text-center py-2 ">YOUR ORDER</h4>
    <table class="table w-50 mx-auto table-bordered table-hover my-shadow">

      <tbody>
        <tr>
          <td>Total Items</td>
          <td class="fs-5 fw-bold"> {{usersCart.cartItems.length}}</td>
        </tr>
        <tr>
          <td>Total Price</td>
          <td class="fs-5 fw-bold text-danger"> {{userPaymentInfo.totalAmount}}$</td>
        </tr>
        <tr>
          <td>Shipping Charges</td>
          <td class="fs-5 fw-bold text-danger">{{userPaymentInfo.shipingCharges}}$</td>
        </tr>
        <tr>
          <td>Discount</td>
          <td class="fs-5 fw-bold text-primary">{{userPaymentInfo.amountReduced}} $</td>
        </tr>
        <tr>
          <td>You Have to Pay</td>
          <td class="fs-5 fw-bold text-primary">{{userPaymentInfo.amountPaid}} $</td>
        </tr>
      </tbody>
    </table>
  </section>

  <section class="m-3 py-4 border border-dark my-rounded-corner">
    <h4 class="d-flex fw-bold justify-content-between align-items-center bg-primary text-light">Payment Method</h4>
    <div class="d-flex justify-content-center align-items-center">
      <h5 class="me-4">Select any Method</h5>
      <select class="form-select w-25" [formControl]="selectedPaymentMethod">
        <option value="0">Open this select menu</option>
        <option *ngFor="let paymentMethod of paymentMethods" value="{{paymentMethod.id}}">
          {{paymentMethod.type}}
          <span *ngIf="paymentMethod.provider !== ''">{{" - "+paymentMethod.provider}}</span>
        </option>
      </select>
    </div>
  </section>

  <section class="m-3 p-4 border border-dark my-rounded-corner">
    <h4 class="w-25 border border-secondary text-center py-2 my-rounded-corner">Proceed to Payments</h4>
    <div class="mt-4 w-50" *ngIf="selectedPaymentMethodName !== ''">
      <table class="table table-bordered my-shadow">
        <thead>
          <tr>
            <td>You have to Pay</td>
            <td> {{userPaymentInfo.amountPaid}} </td>
          </tr>
          <tr>
            <td>Payment Method</td>
            <td> {{this.getPaymentMethod(selectedPaymentMethodName)}} </td>
          </tr>
          <tr>
            <td>Address</td>
            <td *ngIf="true">{{address}} </td>
            <td *ngIf="!true">
              You have not entered address.
              <a  >please enter that</a>
            </td>
          </tr>
          <tr>
            <td>Mobile Number</td>
            <td *ngIf="true">88888888
              <button >change</button>
            </td>
            <td *ngIf="!true">you have not entered mobile number
              <a  >please enter that</a>
            </td>
          </tr>
        </thead>
      </table>

      <div class="d-grid w-50">
        <button type="button" class="btn btn-outline-success mb-4" (click)="placeOrder()">
          Pay
        </button>
      </div>

      <div class="processing">
        <div class="spinner-border me-4" role="status" *ngIf="displaySpinner">
          <span class="visually-hidden">Loading...</span>
        </div>
        <h6 class="m-0 text-danger"> {{message}} </h6>
      </div>
    </div>
  </section>

</div>
