import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { NavigationService } from '../services/navigation.service';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { User } from '../models/user';
import { UserAuthenticationService } from '../services/user-authentication.service';

@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.css']
})
export class AccountComponent implements OnInit{

  accountForm!: FormGroup;
  message = '';

  constructor(
    private navigationService: NavigationService,
    private route: ActivatedRoute,
    private router: Router,
    private fb: FormBuilder,
    private userAuthentication:UserAuthenticationService
  ) {}


  ngOnInit(): void {
    this. accountForm = new FormGroup({
      firstname: new FormControl(),
      lastname: new FormControl(),
      email: new FormControl(),
      DatumZaposlenja: new FormControl(),
      address: new FormControl(),
      Aktivan: new FormControl(true),
      id: new FormControl(),
    });
    if (this.route.snapshot.queryParams['account-details'] == 'true') {
      this.navigationService
        .getActiveCartOfUser(this.route.snapshot.params['id'])
        .subscribe((data: any) => {
          this.accountForm.setValue(data);
        });
    }
  }

  // ngOnInit(): void {
  //   this.accountForm = this.fb.group({
  //     firstname: [''],
  //     lastname: [''],
  //     email: [''],
  //     address: [''],
  //     mobile: [''],
  //   });

  //   this.route.params.subscribe(params => {
  //     const userId = params['id'];
  //     const isEditMode = params['account-details'] === 'true';

  //     console.log('UserID:', userId, 'Edit Mode:', isEditMode);

  //     if (isEditMode) {
  //       this.navigationService.getActiveCartOfUser(userId)
  //         .subscribe(
  //           (data: any) => {
  //             console.log('User Data:', data);
  //             this.accountForm.patchValue(data);
  //           },
  //           (error) => {
  //             console.error('Error fetching user data:', error);
  //           }
  //         );
  //     }
  //   });
  // }


  save() {
    let noviZaposleni: User;
    noviZaposleni = this.accountForm.getRawValue();
    console.log(noviZaposleni);
    this.navigationService.save(noviZaposleni).subscribe(data =>{
      this.goBack();
    });

  }

  goBack() {
    this.router.navigate(['']);
  }

  updateUser() {
    const updatedUserData = this.accountForm.getRawValue(); // Assuming your form matches the user data structure
    const userId = this.userAuthentication.getUser().id;

    this.navigationService.updateUser(userId, updatedUserData).subscribe(
      (response) => {
        // Handle successful update
        console.log('User data updated successfully');
      },
      (error) => {
        // Handle update error
        console.error('Error updating user data:', error);
      }
    );
  }



}
<h1>Account info</h1>
<form [formGroup]="accountForm"  (ngSubmit)="save()">

  <div class="row mb-3">
    <div class="col">
      <label for="firstname" class="form-label">First Name</label>
      <input type="text" class="form-control" id="firstname" formControlName="firstname">

      <div class="col">
        <label for="lastname" class="form-label">Last Name</label>
        <input type="text" class="form-control" id="lastname" formControlName="lastname">

      </div>
    </div>

    <div class="mb-3">
      <label for="email" class="form-label">Email</label>
      <input type="text" class="form-control" id="email" formControlName="email">


    </div>
    <div class="mb-3">
      <label for="address" class="form-label">Address</label>
      <input type="text" class="form-control" id="address" formControlName="address">

    </div>

    <div class="mb-3">
      <label for="mobile" class="form-label">Mobile</label>
      <input type="text" class="form-control" id="mobile" formControlName="mobile">

    </div>


  </div>


  <!-- account button -->
  <div class="mx-auto text-center">
    <button mat-raised-button color="primary" (click)="save()">Zapamti</button>
    <button mat-raised-button color="accent" (click)="goBack()">Nazad</button>
  </div>
</form>
