@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {

}
<footer class="w-100 h-100 bg-light my-shadow d-flex justify-content-around align-items-center fs-4">
  <div>
      <span class="me-3">Made By:</span>
      <span class="fw-bold fst-italic">Rohan Ankalkote</span>
  </div>

  <div>
      <span>Created In: August 2022</span>
  </div>

  <div>
      <span>&copy; Copyright 2022</span>
  </div>
</footer>
