import { HttpClient, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { map } from 'rxjs';
import { Category } from '../models/category';

@Injectable({
  providedIn: 'root'
})
export class NavigationService {
  baseurl = 'https://localhost:7264/api/Shopping/'
  items: any;

  constructor(private http: HttpClient) { }

  getCategoryList() {
    let url = this.baseurl + 'GetCategoryList'
    return this.http.get<any[]>(url).pipe(
      map((categories) =>
        categories.map((category) => {
          let mappedCategory: Category = {
            categoryId: category.categoryId,
            category: category.category,
            subCategory: category.subCategory
          };
          return mappedCategory
        })
      )
    )
  }

  getProducts(category: string, subCategory: string, count: number) {
    return this.http.get<any[]>(this.baseurl + 'GetProducts', {
      params: new HttpParams()
        .set('category', category)
        .set('subCategory', subCategory)
        .set('count', count)
    })
  }

  getProdcut(id: number) {
    let url = this.baseurl + "GetProduct/" + id
    return this.http.get(url)
  }

  addToCard(userid: number, productid: number) {
    let url = this.baseurl + 'InsertCartItem/' + userid + '/' + productid;
    return this.http.post(url, null, { responseType: 'text' })
  }

  getAllReviewsOfProduct(productId: number) {
    let url = this.baseurl + 'GetProductReviews/' + productId;
    return this.http.get(url);
  }

}
